import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";

import "../App.css";

function Lobby() {
  const navigate = useNavigate();
  const [socket, setSocket] = useState();

  const handleButtonClick = (blockId) => {
    navigate(`/codeBlocks/${blockId}`);
  };

  // Handle self text change.
  useEffect(() => {
    if (socket == null) return;
    socket.emit("send-changes");
    const handler = (codeBlocks) => {
      console.log(codeBlocks);
    };

    // "text-change" is quill built-in event
    socket.on("get-blocks", handler);
    return () => {
      socket.off("get-blocks", handler);
    };
  }, [socket]);

  // Configure server connection.
  useEffect(() => {
    const s = io("http://localhost:3001");
    setSocket(s);
    return () => {
      s.disconnect();
    };
  }, []);

  const codeBlocksTitles = [];

  const codeBlocks = [
    { blockId: "codeBlock1" },
    { blockId: "codeBlock2" },
    { blockId: "codeBlock3" },
    { blockId: "codeBlock4" },
  ];

  return (
    <div className="container-c">
      <h1>Choose Code Block</h1>
      <div className="buttons-div">
        {codeBlocks.map((codeBlock) => (
          <button
            onClick={() => handleButtonClick(codeBlock.blockId)}
            key={codeBlock.blockId}
          >
            {codeBlock.blockId}
          </button>
        ))}
      </div>
    </div>
  );
}

export default Lobby;
