import React, { useCallback, useEffect, useRef, useState } from "react";
import "quill/dist/quill.snow.css";
import Quill from "quill";
import { io } from "socket.io-client";

function TextEditor() {
  const [socket, setSocket] = useState();
  const [quill, setQuill] = useState();

  // Configure server connection.
  useEffect(() => {
    const s = io("http://localhost:3001");
    setSocket(s);
    return () => {
      s.disconnect();
    };
  }, []);

  // Configure text-editor.
  const wrapperRef = useCallback((wrapper) => {
    if (wrapper == null) return;

    // Makes sure that editor appears only once each rendering.
    wrapper.innerHTML = "";
    const editor = document.createElement("div");
    wrapper.append(editor);

    const q = new Quill(editor, {
      theme: "snow",
      modules: {
        toolbar: false,
      },
      formats: [],
    });
    setQuill(q);
  }, []);

  // Handle quill-editor text change.
  useEffect(() => {
    if (socket == null || quill == null) return;

    const handler = (delta, oldDelta, source) => {
      // Emit an event only for others text changes.
      if (source !== "user") return;
      // Send only the spcific text changes, not all doc.
      socket.emit("send-changes", delta);
    };

    // "text-change" is quill built-in event
    quill.on("text-change", handler);
    return () => {
      quill.off("text-change", handler);
    };
  }, [socket, quill]);

  return <div id="container" ref={wrapperRef}></div>;
}

export default TextEditor;
